import React, { useContext, useEffect, useState } from 'react';
import { Container } from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { fetchBrands, fetchDevices, fetchTypes } from "../http/deviceAPI";
import Pages from "../components/Pages";
import Slider from '@mui/material/Slider';

// import { useCookies } from '../services/cookie';
// import { withCookies } from '../services/cookie';

// const COOKIE_KEY = 'fa2'
// const [cookie, setCookie] = useCookies(COOKIE_KEY);

// let gsegr = JSON.parse(cookie('fa2'))
// setCookie('gserger')

const Shop = observer(() => {
    const { device } = useContext(Context)
    const [value, setValue] = useState([0, 131230])

    const updateRange = (e, data) => {
        setValue(data)
    }


    // useEffect(() => {
    //     device.brands.forEach(brand => {
    //         if (brand.id === 1)
    //             device.setSelectedBrand(brand)
    //     });
    // }, [])


    // useEffect(() => {
    //     fetchTypes().then(data => device.setTypes(data))
    //     fetchBrands().then(data => device.setBrands(data))
    //     fetchDevices(null, 1, 1, 9, value).then(data => {
    //         device.setDevices(data.rows)
    //         device.setTotalCount(data.count)
    //     })
    // }, [])

    const fawefe2f = (dataa, value1, value2) => {
        let result = { count: 0, rows: [] }
        dataa.forEach(element => {
            if (value1 <= element.price && element.price <= value2) {
                result.rows.push(element);
                result.count += 1;
            }
        });
        return result;
    }

    const maxPrice = (dataa) => {
        let maxPrice = 0;
        dataa.forEach(element => {
            if (element.price > maxPrice) {
                maxPrice = element.price
            }
        });
        return maxPrice;
    }

    useEffect(() => {
        fetchDevices().then(data => {
            let maxPricee = maxPrice(data.rows);
            device.setAllDevices(data.rows)
            device.setMaxPrice(maxPricee)
            setValue([0, maxPricee])
        })
    }, [])

    useEffect(() => {
        let result = fawefe2f(device.allDevices, value[0], value[1])
        device.setDevices(result.rows)
    }, [device.page, device.selectedType, device.selectedBrand, value])


    console.log(value[1]);

    return (
        <div className="myContainer">
            <Row className="mt-2">
                <Col md={3}>
                    <div className="filter">
                        <h4>Фильтр подбора</h4>
                        <div className="block-filter">
                            <p>Цена</p>
                            <div className="inputs-range-slider">
                                <input
                                    value={value[0]}
                                    onChange={e => setValue([e.target.value, value[1]])}
                                />
                                <input
                                    value={value[1]}
                                    onChange={e => setValue([value[0], e.target.value])}
                                />
                            </div>
                            <div>
                                <Slider
                                    sx={{
                                        marginTop: 2,
                                        width: '100%',
                                        color: 'green',
                                    }}
                                    min={0}
                                    max={device.maxPrice}
                                    step={10}
                                    value={value}
                                    onChange={updateRange}
                                />
                            </div>
                        </div>
                        <div className="block-filter">
                            <p>Материалы</p>
                            <label>
                                <input type="checkbox" />
                                <span style="">Пластик</span>
                            </label>
                        </div>
                    </div>
                    <TypeBar />
                </Col>
                <Col md={9}>
                    {/* <BrandBar /> */}
                    <DeviceList />
                    <Pages />
                </Col>
            </Row>
        </div>
    );
});

export default Shop;
