import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import { Form, Button, Image, Row, Col } from "react-bootstrap";
import { createType } from "../../http/deviceAPI";

const CreateType = ({ device, show, onHide }) => {
    const [value, setValue] = useState('')

    const addInCart = (dev) => {
        let locStor = (JSON.parse(localStorage.getItem('cart')));
        if (locStor === null) {
            locStor = []
        }
        let result = [];
        if (locStor.length !== 0) {
            let OnlyNumberLocStor = []
            locStor.forEach(e => {
                OnlyNumberLocStor.push(e.id)
            })
            if (locStor.includes(dev.id)) {
                const newArr = locStor.filter(item => item !== dev.id)
                result = newArr;
            } else {
                const newArr = locStor;
                newArr.push(dev.id)
                result = newArr;
            }
        } else {
            const newArr = locStor;
            newArr.push(dev.id)
            result = newArr;
        }
        localStorage.setItem('cart', JSON.stringify(result))
        device.setCart(result)
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton className='gsergerg'>
                <Modal.Title id="contained-modal-title-vcenter">
                    Товар добавлен в корзину
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Row className="pt-4 pb-4">
                    <Col md={2}>
                    <Image style={{ width: '100%' }} src={process.env.REACT_APP_API_URL + device.img} />
                    </Col>
                    <Col md={5}>
                        <span>{device.name}</span>
                    </Col>
                    <Col md={2}>
                        <span>rthdrt</span>
                    </Col>
                    <Col md={2}>
                        <span>{device.price} руб.</span>
                    </Col>
                </Row>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addInCart}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateType;
