import React, { useState, useContext, useEffect } from 'react';
import { NavLink } from "react-router-dom";
import { FAVORITE_ROUTE, BASKET_ROUTE, LOGIN_ROUTE, SHOPE_ISKUS_YELKI_ROUTE, SHOP_ROUTE, SEARCH_ROUTE } from "../utils/consts";
import logo from '../assets/logo-elkitorg.png'
import favorite from '../assets/favorite.png'
import search from '../assets/search.png'
import shopping from '../assets/shopping-cart.png'
import Image from "react-bootstrap/Image";
import { useCookies } from 'react-cookie';
import { color } from '@mui/system';
import { Context } from "../index";


export const MyNavBar = () => {
    const { device } = useContext(Context)
    const [cookies, setCookie] = useCookies();
    const [searchValue, setSearchValue] = useState('')
    useEffect(() => {
        device.setSearch(searchValue)
        // console.log(searchValue);
    }, [searchValue])
    const howItemCart = () => {
        if (cookies.id) {
            const arrCookieId = cookies.id.split('.').map(Number);
            let unicArr = []
            arrCookieId.forEach(element => {
                if (!unicArr.includes(element)) {
                    unicArr.push(element)
                }
            });
            return unicArr.length
        } else {
            return 0
        }
    }
    const allPriceProduct = () => {
        let locStor = (JSON.parse(localStorage.getItem('cart')));
        if (locStor.length === 0 || locStor === null) {
            return 0
        } else {
            return 
        }
    }


    return (
        <div className="MyNavBar">
            <div className="up-navbar">
                <div className="myContainer">
                    <nav className="up-navbar-content">
                        <ul>
                            <li><NavLink to={LOGIN_ROUTE}>О компании</NavLink></li>
                            <li><NavLink to={SHOP_ROUTE}>Доставка и оплата</NavLink></li>
                            <li><NavLink to={SHOP_ROUTE}>Контакты</NavLink></li>
                        </ul>
                    </nav>
                </div>
            </div>
            <div className="center-navbar">
                <div className="myContainer">
                    <div className="center-navbar-content">
                        <NavLink to={SHOPE_ISKUS_YELKI_ROUTE}>
                            <Image width={100} height={100} src={logo} />
                        </NavLink>
                        <div className="search">
                            <input
                                style={{ height: "100%", width: 700 }}
                                placeholder="Поиск по товарам"
                                value={searchValue}
                                onChange={e => setSearchValue(e.target.value)}
                            />
                            <NavLink style={{ padding: "0 10px" }} to={SEARCH_ROUTE}>
                                <Image width="20" src={search}></Image>
                            </NavLink>
                        </div>
                        <div style={{ borderRight: "1px solid #e5e5e5" }}>
                            <NavLink style={{ padding: "0 30px" }} to={FAVORITE_ROUTE}>
                                <Image style={{ opacity: 0.1 }} width={21} height={19} src={favorite} />
                            </NavLink>
                        </div>
                        <NavLink style={{ color: '#4d4d4d' }} to={BASKET_ROUTE}>
                            <div className="d-flex">
                                <Image width="40" height="40" style={{marginRight: 20}} src={shopping}></Image>
                                <div>
                                    <div style={{ display: 'flex' }}>
                                        <div>
                                            Корзина
                                        </div>
                                        <div>
                                            ({howItemCart()})
                                        </div>
                                    </div>
                                    <div>
                                        {allPriceProduct()}
                                    </div>
                                </div>
                            </div>
                        </NavLink>
                    </div>
                </div>
            </div>
            <div className="down-navbar">
                <div className="myContainer">
                    <div className="down-navbar-content">
                        <ul>
                            <li><NavLink className="down-navbar-content-navlink" to={SHOPE_ISKUS_YELKI_ROUTE}><p>Искусственные елки</p></NavLink></li>
                            <li><NavLink className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Высотные елки</p></NavLink></li>
                            <li><NavLink className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Электрогирлянды</p></NavLink></li>
                            <li><NavLink className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Шары</p></NavLink></li>
                            <li><NavLink className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Елочные украшения</p></NavLink></li>
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

// export default MyNavBar;
