import React from 'react'

export const Pagination = ({ currentProduct, totalProduct}) => {
    console.log(totalProduct);
    const pageNumbers = []

    for (let i = 1; i <= Math.ceil(totalProduct / currentProduct); i++) {
        pageNumbers.push(i)
    }
    console.log(Math.ceil(totalProduct / currentProduct));
    console.log(pageNumbers);
    return (
        <div>
            <ul className="pagination">
                {
                    pageNumbers.map(number => (
                        <li className="page-item" key={number}>
                            {number}
                        </li>
                    ))
                }
            </ul>
        </div>
    )
}
