import React, {useContext} from 'react'
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";

const Searh = () => {
    const { device } = useContext(Context)
    fetchDevices().then(data => {
        let maxPricee = maxPrice(data.rows);
        let maxHeightt = maxHeight(data.rows)
        device.setMaxHeight(maxHeightt)
        device.setAllDevices(data.rows)
        device.setMaxPrice(maxPricee)
        console.log(maxHeightt);
        setvalueHeight([0, maxHeightt])
        setValue([0, maxPricee])
    })
    return (
        <div className="myContainer">
            <h1 style={{ padding: '20px 0' }}>Товары по вашему запросу</h1>
            <Col md={9}>
                    <DeviceList currentProduct={device.allDevices} />
            </Col>
        </div>
    )
}

export default Searh;
