import React, { useContext, useEffect, useState } from 'react';
import { Container } from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { fetchBrands, fetchDevices, fetchTypes } from "../http/deviceAPI";
import Pages from "../components/Pages";
import Slider from '@mui/material/Slider';

// import { useCookies } from '../services/cookie';
// import { withCookies } from '../services/cookie';

// const COOKIE_KEY = 'fa2'
// const [cookie, setCookie] = useCookies(COOKIE_KEY);

// let gsegr = JSON.parse(cookie('fa2'))
// setCookie('gserger')

const Shop = observer(() => {

    const { device } = useContext(Context)
    const [value, setValue] = useState([0, 131230])
    const [selectTypes, setSelectTypes] = useState([])

    const updateRange = (e, data) => {
        setValue(data)
    }
    const setOrDeleteTypes = (id) => {
        let gser = true;
        if(selectTypes !== 0) {
            selectTypes.forEach(element => {
                if(element === id) {
                    gser = false;
                }
            });
        }
        let result = selectTypes
        if(gser === true) {
            result.push(id)
            console.log(result); 
            setSelectTypes(result)
        } 
        if(gser === false) {
            let newArr = result.filter(item => item !== id)
            console.log(newArr);
            setSelectTypes(newArr)
        }
        
    }

    // useEffect(() => {
    //     device.brands.forEach(brand => {
    //         if (brand.id === 1)
    //             device.setSelectedBrand(brand)
    //     });
    // }, [])


    useEffect(() => {
        console.log(selectTypes);
    }, [selectTypes])

    const fawefe2f = (dataa, value1, value2) => {
        let result = { count: 0, rows: [] }
        dataa.forEach(element => {
            if (value1 <= element.price && element.price <= value2) {
                result.rows.push(element);
                result.count += 1;
            }
        });
        const selectedTypes = device.selectedTypes;
        let newArr = {count: 0, rows: []} 
        if(selectedTypes.length !== 0) {
            result.rows.forEach(element => {
                if(selectedTypes.includes(element)) {
                    newArr.push(element)
                }
            });
            return newArr;
        }
        return result;
    }

    const maxPrice = (dataa) => {
        let maxPrice = 0;
        dataa.forEach(element => {
            if (element.price > maxPrice) {
                maxPrice = element.price
            }
        });
        return maxPrice;
    }

    useEffect(() => {
        fetchDevices().then(data => {
            let maxPricee = maxPrice(data.rows);
            device.setAllDevices(data.rows)
            device.setMaxPrice(maxPricee)
            setValue([0, maxPricee])
        })
        fetchTypes().then(data => device.setTypes(data))
    }, [])

    useEffect(() => {
        let result = fawefe2f(device.allDevices, value[0], value[1])
        device.setDevices(result.rows)
    }, [device.page, device.selectedType, device.selectedBrand, value])


    return (
        <div className="myContainer">
            <h1 style={{ padding: '20px 0' }}>Искуственные елку</h1>
            <Row className="mt-2">
                <Col md={3}>
                    <div className="filter">
                        <h4>Фильтр подбора</h4>
                        <div className="block-filter">
                            <p>Цена</p>
                            <div className="inputs-range-slider">
                                <input
                                    value={value[0]}
                                    onChange={e => setValue([e.target.value, value[1]])}
                                />
                                <input
                                    value={value[1]}
                                    onChange={e => setValue([value[0], e.target.value])}
                                />
                            </div>
                            <div>
                                <Slider
                                    sx={{
                                        marginTop: 2,
                                        width: '100%',
                                        color: 'green',
                                    }}
                                    min={0}
                                    max={device.maxPrice}
                                    step={10}
                                    value={value}
                                    onChange={updateRange}
                                />
                            </div>
                        </div>
                        <div className="block-filter">
                            <p>Материалы</p>
                            <div className="filterButton">
                                {device.types.map(item =>
                                <label key={item.id}>
                                    <input type="checkbox" onClick={() => setOrDeleteTypes(item.id)} />
                                    <span style={{ paddingLeft: 10 }}>{item.name}</span>
                                </label>
                                )}
                            </div>
                        </div>
                    </div>
                    <TypeBar />
                </Col>
                <Col md={9}>
                    {/* <BrandBar /> */}
                    <DeviceList />
                    <Pages />
                </Col>
            </Row>
        </div>
    );
});

export default Shop;
