import React, {useContext} from 'react'
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";
import { fetchColor, fetchDevices, fetchTypes, fetchMaterials, fetchMadeCountries, fetchProductClass } from "../http/deviceAPI";

const Searh = () => {
    const { device } = useContext(Context)
    fetchDevices().then(data => {
        device.setAllDevices(data.rows)
    })
    return (
        <div className="myContainer">
            <h1 style={{ padding: '20px 0' }}>Товары по вашему запросу</h1>
            <Col md={9}>
                    <DeviceList currentProduct={device.allDevices} />
            </Col>
        </div>
    )
}

export default Searh;
