import React, { useState, useContext} from 'react';
import Modal from "react-bootstrap/Modal";
import { Form, Button, Image, Row, Col } from "react-bootstrap";
// import { Context } from "../../index";

const AddToCart = ({ devicee, show, onHide }) => {
    const { device } = useContext(Context)
    const [value, setValue] = useState(1)

    const addInCart = () => {
        let locStor = (JSON.parse(localStorage.getItem('cart')));
        if (locStor === null) {
            locStor = []
        }
        let result = locStor;
        result.push({id: devicee.id, count: value})
        localStorage.setItem('cart', JSON.stringify(result))
        // device.setCart(result)
    }
    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton className='gsergerg'>
                <Modal.Title id="contained-modal-title-vcenter">
                    Товар добавлен в корзину
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Row className="pt-4 pb-4">
                    <Col md={2}>
                        <Image style={{ width: '100%' }} src={process.env.REACT_APP_API_URL + devicee.img} />
                    </Col>
                    <Col md={5}>
                        <span>{devicee.name}</span>
                    </Col>
                    <Col md={2}>
                        <span>rthdrt</span>
                    </Col>
                    <Col md={2}>
                        <span>{devicee.price} руб.</span>
                    </Col>
                </Row>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addInCart}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddToCart;
