import React, { useEffect, useState } from 'react';
import { Button, Card, Col, Container, Image, Row } from "react-bootstrap";
import bigStar from '../assets/bigStar.png'
import { useParams } from 'react-router-dom'
import { fetchOneDevice, fetchSomeDevice } from "../http/deviceAPI";
import { useCookies } from 'react-cookie';

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] })
    const [cookies, setCookie] = useCookies();
    const { id } = useParams()
    const gawege = [1, 2, 3]

    useEffect(() => {
        fetchOneDevice(id).then(data => setDevice(data))
    }, [])

    const korzina = (dev) => {
        if (cookies.id) {
            const fesf = dev.id
            const cookieNow = `${cookies.id}.${fesf}`
            setCookie('id', `${cookieNow}`)
            console.log(cookieNow);
        } else {
            setCookie('id', `${dev.id}`)
            console.log('gsergersg');
        }
    }

    return (
        <div className="myContainer">
            <h1>{device.name}</h1>
            <Row>
                <Col md={4}>
                    <Image width={'100%'} src={process.env.REACT_APP_API_URL + device.img} />
                </Col>
                <Col md={4}>
                    <div className="specifications">
                        {device.info.map((info, index) =>
                            <Row k>
                                {info.title}: {info.description}
                            </Row>
                        )}
                    </div>
                </Col>
                <Col md={4}>
                    <Card
                        className="by-card-device"
                    >
                        <span className="price-on-device-page">{device.price} руб.</span>
                        <span className='gaerger'>в наличии</span>
                        <Row className="card-btn-group">
                            <Col md={6}>
                                <button className="btn-product-go-cart" onClick={() => korzina(device)}>В корзину</button>
                            </Col>
                            <Col md={6}>
                                <button className="btn-buy-for-one-click">Купить в 1 клик</button>
                            </Col>
                        </Row>
                    </Card>
                </Col>
            </Row>
        </div>
    );
};

export default DevicePage;
