import React, {useContext, useEffect} from 'react';
import {Container} from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {fetchBrands, fetchDevices, fetchTypes} from "../http/deviceAPI";
import Pages from "../components/Pages";

const Shop = observer(() => {
    const {device} = useContext(Context)

    useEffect(() => {
        device.brands.forEach(brand => {
            if(brand.id === 7)
            device.setSelectedBrand(brand)
        });
    }, [])


    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data))
        fetchBrands().then(data => device.setBrands(data))
        fetchDevices(null, 7, 1, 7).then(data => {
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
        })
    }, [])

    useEffect(() => {
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 7).then(data => {
            // console.log(data);
            // const gesrg = fawefef(data.rows)
            // console.log(gesrg);

            // .map(dat => console.log(dat))
            // console.log(result1);
            // dat => result.push(dat)
            // console.log(result);
            // console.log(data.rows[1].price);
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
        })
    }, [device.page, device.selectedType, device.selectedBrand,])

    // const fawefef = (dataa) => {
    //     let result = {count: 0, rows: []}
    //     dataa.forEach(element => {
    //             if(element.price > 1347) {
    //                 result.rows.push(element);
    //             }
    //         });
    //     return(result)
    // }

    return (
        <div className="myContainer">
            <Row className="mt-2">
                <Col md={3}>
                    <TypeBar/>
                </Col>
                <Col md={9}>
                    <BrandBar/>
                    <DeviceList/>
                    <Pages/>
                </Col>
            </Row>
        </div>
    );
});

export default Shop;
