import React, { useState } from 'react'
import { Image, Row } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { FAVORITE_ROUTE, BASKET_ROUTE, LOGIN_ROUTE, SHOPE_ISKUS_YELKI_ROUTE, SHOP_ROUTE, SEARCH_ROUTE } from "../utils/consts";
import favorite from '../assets/favorite.png'
import search from '../assets/search.png'
import shopping from '../assets/shopping-cart.png'
import { observer } from "mobx-react-lite";

const MobileNavbar = observer(() => {
    const [catalogOpen, setCatalogOpen] = useState(false)
    const [blackBg, setBlackBg] = useState(false)

    const catalog = () => {
        if (catalogOpen === true) {
            const body = document.querySelector('body');
            body.classList.remove('no-scroll')
            setCatalogOpen(false)
            setBlackBg(false)
        } else {
            const body = document.querySelector('body');
            body.classList.add('no-scroll')
            setCatalogOpen(true)
            setBlackBg(true)
        }
    }
    const allClose = () => {
        const body = document.querySelector('body');
        body.classList.remove('no-scroll')
        setCatalogOpen(false)
        setBlackBg(false)
    }

    return (
        <div className={catalogOpen ? 'm-navbar m-navbar-active' : 'm-navbar'}>
            <div className="m-navbar-menu">
                <ul>
                    <li onClick={() => catalog()}>
                        <Image className="icon-nav-m" src={search}></Image>
                    </li>
                    <li>
                        <NavLink className="nav-link-m" to={BASKET_ROUTE}>
                            <Image className="icon-nav-m" src={shopping}></Image>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="nav-link-m" to={FAVORITE_ROUTE}>
                            <Image className="icon-nav-m" src={favorite}></Image>
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="nav-link-m" to={SEARCH_ROUTE}>
                            <Image className="icon-nav-m" src={search}></Image>
                        </NavLink>
                    </li>
                </ul>
            </div>
            <div className={catalogOpen ? 'catalog-nav-m-active ' : 'catalog-nav-m'}>
                <ul>
                    <li><NavLink onClick={() => allClose()} className="down-navbar-content-navlink" to={SHOPE_ISKUS_YELKI_ROUTE}><p>Искусственные елки</p></NavLink></li>
                    <li><NavLink onClick={() => allClose()} className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Высотные елки</p></NavLink></li>
                    <li><NavLink onClick={() => allClose()} className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Электрогирлянды</p></NavLink></li>
                    <li><NavLink onClick={() => allClose()} className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Шары</p></NavLink></li>
                    <li><NavLink onClick={() => allClose()} className="down-navbar-content-navlink" to={SHOP_ROUTE}><p>Елочные украшения</p></NavLink></li>
                </ul>
            </div>
            <div onClick={() => allClose()} className={blackBg ? 'black-back-active' : 'black-back'}>

            </div>
            <div onClick={() => allClose()} className={blackBg ? 'search' : 'black-back'}>

            </div>
        </div>
    )
})

export default MobileNavbar;
