import React, { useEffect, useContext, useState } from 'react';
import { Context } from "../index";
import { fetchDevices } from "../http/deviceAPI";
import { useCookies } from 'react-cookie';
import { Button, Card, Col, Container, Image, Row } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { SHOPE_ISKUS_YELKI_ROUTE, DEVICE_ROUTE } from "../utils/consts";

const Checkout = () => {
    const [cookies, setCookie] = useCookies();
    const [devicee, setDevicee] = useState([])
    const [cartNoEmpty, setCartNoEmpty] = useState(false)
    const { device } = useContext(Context)

    const cookieId = cookies.id;

    const searchId = (devices, ids) => {
        let aarr = [];
        devices.forEach(element => {
            if (ids.includes(element.id)) {
                aarr.push(element)
            }
        });
        return aarr;
    }
    const removePost = (idd) => {
        let result = devicee.filter(item => item.id !== idd)
        localStorage.setItem('cart', JSON.stringify(result))
        setDevicee(result)
        device.setCart(result)
    }
    useEffect(() => {
        let locStor = (JSON.parse(localStorage.getItem('cart')));
        if (locStor === null) {
            locStor = []
        }
        setDevicee(locStor)
    }, [])

    useEffect(() => {
        if (devicee.length !== 0) {
            setCartNoEmpty(true)
        } else {
            setCartNoEmpty(false) 
        }
    }, [devicee])

    useEffect(() => {
        if (cookieId) {
            fetchDevices().then(data => {
                const gserg = data.rows
                const arrCookieId = cookieId.split('.').map(Number);
                const res = searchId(gserg, arrCookieId)
                // setDevice(res)
            })
        }
    }, [cookieId])

    const orderPrice = () => {
        let result = 0;
        devicee.forEach(element => {
            result += element.price
        });
        return result
    }
    console.log(devicee);
    return (
        <div className="myContainer">
            {cartNoEmpty ?
                <div>
                    <h1 style={{margin: "20px 0", fontSize: 48}}>Оформление заказа</h1>
                    <Row>
                        <Col md={8}>
                            {/* {devicee.map(dev =>
                                <Row className="item-basket" key={dev.id}>
                                    <Col md={2}>
                                        <Image style={{ width: '100%' }} src={process.env.REACT_APP_API_URL + dev.img} />
                                    </Col>
                                    <Col md={4}>
                                        <NavLink className='name-product-in-basket' to={DEVICE_ROUTE + '/' + dev.id}>{dev.name}</NavLink>
                                    </Col>
                                    <Col md={3}>
                                        <p className="price-item-basket">{dev.price} руб.</p>
                                    </Col>
                                    <Col md={3}>
                                        <button onClick={() => removePost(dev.id)}>Удалить</button>
                                    </Col>
                                </Row>
                            )} */}
                            <h4>1. Контактная информация</h4>
                            <Row>
                                <input type="text" />
                                <input type="text" />
                            </Row>
                        </Col>
                        <Col md={4}>
                            <div className='right-receipt'>
                                <span style={{ fontSize: 17 }}>Итоговая сумма:</span>
                                <span className="order-price">{orderPrice()} руб.</span>
                                <button className="main-btn">Оформить заказ</button>
                            </div>
                        </Col>
                    </Row>
                </div>
                :
                <div className="empty-cart">
                    <h2>В корзине пока ничего нет</h2>
                    <p className="description-basket">Вы можете начать свой выбор с главной страницы или воспользоваться поиском, если ищете что-то конкретное.</p>
                    <Col md={2}>
                        <NavLink to={SHOPE_ISKUS_YELKI_ROUTE}>
                            <button className="main-btn">Начать покупки</button>
                        </NavLink>
                    </Col>
                </div>}
        </div>
    );
}

export default Checkout;
