import React from 'react';
import {Card, Col} from "react-bootstrap";
import Image from "react-bootstrap/Image";
import star from '../assets/star.png'
import {useHistory} from "react-router-dom"
import {DEVICE_ROUTE} from "../utils/consts";

const DeviceItem = ({device}) => {
    const history = useHistory()
    return (
        <Col md={4} className={"mt-3"} onClick={() => history.push(DEVICE_ROUTE + '/' + device.id)}>
            <Card style={{cursor: 'pointer'}} border={"light"}>
                <Image src={process.env.REACT_APP_API_URL + device.img}/>
                <div>{device.name}</div>
                <div><span>Цена:</span>{device.price}</div>
                <button>gersgersg</button>
            </Card>
        </Col>
    );
};

export default DeviceItem;
