import React from 'react';
import { Card, Col } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import star from '../assets/star.png'
import { useHistory } from "react-router-dom"
import { DEVICE_ROUTE } from "../utils/consts";
import { useCookies } from 'react-cookie';

const DeviceItem = ({ device }) => {
    const history = useHistory()
    const [cookies, setCookie] = useCookies();

    const korzina = (dev) => {
        if(cookies.id) {
            const fesf = dev.id
            const cookieNow = `${cookies.id}.${fesf}`
            setCookie('id', `${cookieNow}`)
            console.log(cookieNow);
        } else {
            setCookie('id', `${dev.id}`)
            console.log('gsergersg');
        }
    }
    useEffect(() => {
        fetchDevices().then(data => {
            let maxPricee = maxPrice(data.rows);
            device.setAllDevices(data.rows)
            device.setMaxPrice(maxPricee)
            setValue([0, maxPricee])
        })
        fetchTypes().then(data => device.setTypes(data))
        fetchMaterials().then(data => device.setMaterials(data))
    }, [])
    const gserg = (mId) => {
        // device.materials.forEach(element => {
        //     if(element.id === mId) {
        //         return element.name
        //     }
        // });
        console.log(device.materials);
        if(mId === 1) {
            return 2;
        }
    }
    return (
        <Col md={4} className={"mb-3"}>
            <Card style={{ cursor: 'pointer' }} border={"light"}>
                <div onClick={() => history.push(DEVICE_ROUTE + '/' + device.id)}>
                    <Image style={{width: '100%'}} src={process.env.REACT_APP_API_URL + device.img} />
                    <p className='product-name'>{device.name}</p>
                    <span>Материал:{gserg(device.materialId)}</span>
                    <div><span>Цена:</span>{device.price}</div>
                </div>
                <div>
                    <button onClick={() => korzina(device)}>В корзину</button>
                    <button>Купить в 1 клик</button>
                </div>
            </Card>
        </Col>
    );
};

export default DeviceItem;
